Customising Tooltop style (Background = White, Font colour = Black)

tooltip_css &lt;- "background-color:white; #&lt;&lt;
font-style:bold; colour:black;" #&lt;&lt;

p &lt;- ggplot(data=exam_data,
            aes(x = MATHS)) + 
  geom_dotplot_interactive(
    aes(tooltip = ID),
    stackgroups = TRUE,
    binwidth = 1,
    method = "histodot") +
  scale_y_continuous(NULL,
                     breaks = NULL
  )

girafe(
  ggobj = p,
  width_svg = 6,
  height_svg = 6*0.618,
  options = list(   #&lt;&lt;
    opts_tooltip(   #&lt;&lt;
      css = tooltip_css)) #&lt;&lt;
)
